import { useReducer } from "react";
import CartContext from "./cart-context";

const defaulCartState = {
  items: [],
  totalAmount: 0,
};
const CartReducer = (state, action) => {
  if (action.type === "ADD") {
    const updatedTotalAmount =
      state.totalAmount + action.item.price * action.item.amount;

    const indexOfItemIfPresent = state.items.findIndex(
      (item) => item.id === action.item.id
    );
    let updatedItems;
    const prevItem = state.items[indexOfItemIfPresent];
    console.log(prevItem);
    console.log(indexOfItemIfPresent);

    if (indexOfItemIfPresent) {
      console.log(prevItem);
      // const updatedItem = {
      //   ...prevItem,
      //   amount: prevItem.amount + action.item.amount,
      // };
      // updatedItems = [...state.items];
      // updatedItems[indexOfItemIfPresent] = updatedItem;
    } else {
      updatedItems = state.items.concat(action.item);
    }

    return { items: updatedItems, totalAmount: updatedTotalAmount };
  }
  return defaulCartState;
};

const CartProvider = (props) => {
  const [cartState, dispatchCartState] = useReducer(
    CartReducer,
    defaulCartState
  );

  const addItemToCartHandler = (item) => {
    dispatchCartState({ type: "ADD", item: item });
  };
  const removeItemTOCartHandler = (id) => {
    dispatchCartState({ type: "REMOVW", id: id });
  };

  const cartContext = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemToCartHandler,
    removeItem: removeItemTOCartHandler,
  };
  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
