import Button from "../../UI/Button/Button";

import classes from "./MealItemForm.module.css";

const MealItemForm = () => {
  return (
    <form className={classes.form}>
      <label htmlFor="amount">Amount</label>
      <input id="amount" value={1}></input>
      <Button>+Add</Button>
    </form>
  );
};

export default MealItemForm;
