import React, { Fragment } from "react";

import Button from "../UI/Button/Button.js";

const Dish = (props) => {
  return (
    <Fragment>
      {props.meals.map((meal) => {
        return (
          <li>
            <h3>Sushi</h3>
            <p>Freash Sushi</p>
            <h5>100</h5>
            <div>
              <label htmlFor="amount">Amount</label>
              <input id="amount" value={1}></input>
              <Button>+Add</Button>
            </div>
          </li>
        );
      })}
    </Fragment>
  );
};
export default Dish;
