import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [moives, setMovies] = useState();
  const fetchMoivies = () => {
    fetch("https://swapi.dev/api/films")
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        const transformedMoivies = data.results.map((moive) => {
          return {
            id: moive.episode_id,
            title: moive.title,
            openingtext: moive.opening_crawl,
            releasedate: moive.release_date,
          };
        });
        setMovies(data.result);
      });
  };

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoivies}>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={moives} />
      </section>
    </React.Fragment>
  );
}

export default App;
