import { useState } from "react";

const Useinput = (validateValue) => {
  const [enteredField, setEnteredField] = useState("");
  const [FieldIsTouched, setFieldIsTouched] = useState(false);

  const enteredFieldIsValid = validateValue(enteredField);
  const enteredFieldIsInValid = !enteredFieldIsValid && FieldIsTouched;

  return {
    enteredField,
    FieldIsTouched,
    enteredFieldIsValid,
    enteredFieldIsInValid,
  };
};

export default Useinput;
