import { useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [inputFieldIsTouched, setInputFieldIsTouched] = useState(false);

  const EnterdNameisValid = enteredName.trim() !== "";
  const InputIsInvalid = !EnterdNameisValid && inputFieldIsTouched;

  // const [formIsValid, setFormIsValid] = useState(false);
  // useEffect(() => {
  //   if (EnterdNameisValid) {
  //     setFormIsValid(true);
  //   } else {
  //     setFormIsValid(false);
  //   }
  // }, [EnterdNameisValid]);

  let formIsValid = false;
  if (EnterdNameisValid) {
    formIsValid = true;
  }

  const inputChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const inputBlurHandler = () => {
    setInputFieldIsTouched(true);
  };

  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredEmailIsValid, setEnteredEmailIsValid] = useState(false);
  const [emailFieldIsTouched, setEmailFieldIsTouched] = useState(false);
  const enteredEmailIsInValid = !enteredEmailIsValid && emailFieldIsTouched;
  const emialChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
    setEmailFieldIsTouched(true);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    setInputFieldIsTouched(true);
    if (!EnterdNameisValid) {
      return;
    }
    if (!enteredEmail.trim().includes(".com")) {
      setEnteredEmailIsValid(false);
      return;
    }
    setEnteredEmailIsValid(true);
    console.log(enteredName);
    setEnteredName("");
    setEnteredEmail("");
    setInputFieldIsTouched(false);
  };

  const inputClass = InputIsInvalid ? "form-control invalid" : "form-control";

  return (
    <form onSubmit={submitHandler}>
      <div className={inputClass}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={inputChangeHandler}
          value={enteredName}
          onBlur={inputBlurHandler}
        />
      </div>
      {InputIsInvalid && <p className="error-text">EnteredName is not Valid</p>}

      <div className={inputClass}>
        <label htmlFor="email">Your Email</label>
        <input
          type="text"
          id="email"
          onChange={emialChangeHandler}
          value={enteredEmail}
        />
      </div>
      {enteredEmailIsInValid && (
        <p className={"error-text"}>Enter a Valid Email</p>
      )}
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
