// import { useState } from "react";
import useInput from "../hooks/use-input";

const SimpleInput = (props) => {
  const {
    enteredField: enteredName,
    enteredFieldIsValid: enteredNameIsValid,
    enterdNameisInvalid: enterdNameIsInvalid,
    fieldChangeHandler: nameChangeHandler,
    fieldBlurHandler: nameBlurHandler,
    reset,
  } = useInput((value) => value.trim() !== "");

  let formIsValid = false;
  if (enteredNameIsValid) {
    formIsValid = true;
  }

  const submitHandler = (event) => {
    event.preventDefault();

    if (!enteredNameIsValid) {
      return;
    }
    console.log(enteredName);
    reset();
  };

  const inputClass = enterdNameIsInvalid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={submitHandler}>
      <div className={inputClass}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameChangeHandler}
          value={enteredName}
          onBlur={nameBlurHandler}
        />
      </div>
      {enterdNameIsInvalid && (
        <p className="error-text">EnteredName is not Valid</p>
      )}
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
