import { useState } from "react";

const Useinput = () => {
  const [enteredField, setEnteredField] = useState("");
  const [emailFieldIsTouched, setEmailFieldIsTouched] = useState(false);

  const enteredFieldIsValid = enteredField.trim().includes(".com");
  const enteredFieldIsInValid = !enteredFieldIsValid && emailFieldIsTouched;

  const fieldChangeHandler = (event) => {
    setEnteredField(event.target.value);
  };

  const fieldBlurChange = () => {
    setEmailFieldIsTouched(true);
  };

  return {
    enteredField,
    emailFieldIsTouched,
    fieldChangeHandler,
    fieldBlurChange,
  };
};

export default Useinput;
