import { useState } from "react";
import useInput from "../hooks/use-input";

const BasicForm = (props) => {
  const {
    input: enteredFirstName,
    inputIsValid: firstNameIsValid,
    InputIsInvalid: firstNameIsInvalid,
    inputChangeHandler: firstNameInputHandler,
    inputBlurHandler: firstNameBlurHandler,
    reset: resetFirstName,
  } = useInput((val) => val.trim() !== "");

  // const {
  //   input: enteredLastName,
  //   inputIsValid: lastNameIsValid,
  //   InputIsInvalid: lastNameIsInvalid,
  //   inputChangeHandler: lastNameInputHandler,
  //   inputBlurHandler: lastNameBlurHandler,
  //   reset: resetLastName,
  // } = useState((val) => val.trim() !== "");

  let formIsValid = false;
  if (firstNameIsValid) {
    formIsValid = true;
  }

  const submitHandler = (event) => {
    event.preventDefault();

    if (!firstNameIsValid) {
      console.log("Invalid");
      return;
    }
    resetFirstName();
  };

  const nameClass = firstNameIsInvalid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={submitHandler}>
      <div className="control-group">
        <div className={nameClass}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={firstNameInputHandler}
            value={enteredFirstName}
            onBlur={firstNameBlurHandler}
          />
          {firstNameIsInvalid && (
            <p className="error-text">Enter a Valid Name</p>
          )}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
