import { useDispatch } from "react-redux";
import { cartActions } from "../../store/cart-slice";

import Card from "../UI/Card";
import classes from "./ProductItem.module.css";

const DUMMY_DATA = [
  {
    id: "p1",
    price: 10,
    title: "Book",
    description: "good and intersting Book",
  },
  { id: "p2", price: 20, title: "pen", description: "smooth and fancy pen" },
  { id: "p3", price: 30, title: "bag", description: "red mature bag" },
];

const ProductItem = (props) => {
  const dispatch = useDispatch();

  const { title, price, description } = props;

  const addToCartHandler = () => {
    dispatch(cartActions.addToCart({ title, price, description }));
  };

  return (
    <li className={classes.item}>
      {DUMMY_DATA.map((product) => (
        <Card>
          <header>
            <h3>{product.title}</h3>
            <div className={classes.price}>${product.price.toFixed(2)}</div>
          </header>
          <p>{product.description}</p>
          <div className={classes.actions}>
            <button onClick={addToCartHandler}>Add to Cart</button>
          </div>
        </Card>
      ))}
    </li>
  );
};

export default ProductItem;
