import { useEffect, Fragment } from "react";

import { useSelector, useDispatch } from "react-redux";

import { uiActions } from "./store/ui-slice";

import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import Notification from "./components/UI/Notification";

function App() {
  // const [isLoading, setIsLoading] = useState(false);
  // const [didFetched, setDidFetched] = useState(false);
  // const [error, setError] = useState();

  const showCart = useSelector((state) => state.ui.showCart);
  const cart = useSelector((state) => state.cart);
  const notification = useSelector((state) => state.ui.notification);

  const dispatch = useDispatch();

  useEffect(() => {
    const fetchReaquest = async () => {
      dispatch(
        uiActions.showNotification({
          status: "pending",
          title: "Fetching data",
          message: "Loading...",
        })
      );
      const response = await fetch(
        "https://react-http-5f152-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (!response.ok) {
        throw new Error("Unable to fetch the Data");
      }
      dispatch(
        uiActions.showNotification({
          status: "success",
          title: "Success",
          message: "Data is fetched",
        })
      );
    };

    fetchReaquest()
      .then(console.log("sucess"))
      .catch((error) => {
        dispatch(
          uiActions.showNotification({
            status: "error",
            title: "Error",
            message: "Failed to fetch data",
          })
        );
      });
  }, []);

  return (
    <Fragment>
      {error && <Notification title="Error" message={error} status="error" />}
      {isLoading && (
        <Notification title="status" message="Loading..." status="success" />
      )}
      {didFetched && (
        <Notification
          title="status"
          message="fetched Successfully "
          status="success"
        />
      )}
      <Layout>
        {showCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
