import { uiActions } from "./ui-slice";

export const thunkfunc1 = (cart) => {
  return async (dispatch) => {
    dispatch(
      uiActions.showNotification({
        status: "pending",
        title: "Sending...",
        message: "Sending cart data!",
      })
    );
    const fetchRequest = async () => {
      const response = await fetch(
        "https://react-http-5f152-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (!response.ok) {
        throw new Error("Unable to fetch the Data");
      }
    };
    try {
      await fetchRequest();
      dispatch(
        uiActions.showNotification({
          status: "success",
          title: "Success!",
          message: "Sent cart data successfully",
        })
      );
    } catch (error) {
      dispatch(
        uiActions.showNotification({
          status: "error",
          title: "Error!",
          message: "Sending cartdata is failed!",
        })
      );
    }
  };
};

const thunkfunc2 = () => {
  return async (dispatch) => {
    const fetchRequest = async () => {
      const response = await fetch(
        "https://react-http-5f152-default-rtdb.firebaseio.com/products.json"
      );
      const result = await response.json();
    };
    try {
      await fetchRequest();
    } catch (errror) {
      dispatch(
        uiActions.showNotification({
          status: "error",
          title: "Error!",
          message: "Sending cart data is failed!",
        })
      );
    }
  };
};
