import React, { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import "./Expenses.css";
import Card from "../UI/Card";

const Expenses = (props) => {
  const [filteredYear, setfilteredYear] = useState("2020");

  // const [items, setItems] = useState(props.items);

  const selectYearHandler = (selectedYear) => {
    setfilteredYear(selectedYear);
    // console.log("Expenses " + filteredYear);
    // console.log(props.items[0].date.getFullYear());
    // setItems((preitems) => {
    //   return preitems.filter((expense) => {
    //     return expense.date.getFullYear().toString() === filteredYear;
    //   });
    // });
  };

  const filteredExpenses = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === filteredYear;
  });

  let expenses = <h1>Expense Not found</h1>;
  if (filteredExpenses.length > 0) {
    expenses = filteredExpenses.map((expense) => (
      <ExpenseItem
        key={expense.id}
        title={expense.title}
        amount={expense.amount}
        date={expense.date}
      />
    ));
  }

  return (
    <Card className="expenses">
      <ExpensesFilter
        selected={filteredYear}
        onSelectYear={selectYearHandler}
      ></ExpensesFilter>
      {filteredExpenses.length === 0 && expenses}
    </Card>
  );
};

export default Expenses;
