import React, { useState } from "react";
import "./Form.css";

function Form() {
  const [currentSaving, setCurrentSaving] = useState("");
  const [yearSaving, setyearSaving] = useState("");
  const [expectedInterest, setExpectedInterest] = useState("");
  const [investmentDuration, setInvestmentDuration] = useState("");

  // const currentSavingChangeHandler = (event) => {
  //   setCurrentSaving(event.target.value);
  //   console.log(event.target.value);
  // };
  // const yearlyContributionChangeHandler = (event) => {
  //   setyearSaving(event.target.value);
  //   console.log(event.target.value);
  // };
  // const expectedReturnChangeHandler = (event) => {
  //   setExpectedInterest(event.target.value);
  //   console.log(event.target.value);
  // };
  // const investmentDurationChangeHandler = (event) => {
  //   setInvestmentDuration(event.target.value);
  //   console.log(event.target.value);
  // };

  const inputChangeHandler = () => {
    console.log("Changed");
  };

  const calculateHandler = (userInput) => {
    const yearlyData = [];

    let currentSavings = +userInput["current-savings"]; // feel free to change the shape of this input object!
    const yearlyContribution = +userInput["yearly-contribution"]; // as mentioned: feel free to change the shape...
    const expectedReturn = +userInput["expected-return"] / 100;
    const duration = +userInput["duration"];

    // The below code calculates yearly results (total savings, interest etc)
    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      yearlyData.push({
        // feel free to change the shape of the data pushed to the array!
        year: i + 1,
        yearlyInterest: yearlyInterest,
        savingsEndOfYear: currentSavings,
        yearlyContribution: yearlyContribution,
      });
    }
    console.log("hiii");
    // do something with yearlyData ...
  };

  const submitHandler = (event) => {
    event.preventDefault();
    console.log("Submit Button Pressed");
  };

  const resetButtonHandler = () => {
    // setCurrentSaving("");
    // setyearSaving("");
    // setExpectedInterest("");
    // setInvestmentDuration("");
    console.log("Reset Button Pressed");
  };

  return (
    <form className="form" onSubmit={submitHandler}>
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            type="number"
            id="current-savings"
            onChange={currentSavingChangeHandler}
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            type="number"
            id="yearly-contribution"
            onChange={yearlyContributionChangeHandler}
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input
            type="number"
            id="expected-return"
            onChange={expectedReturnChangeHandler}
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            type="number"
            id="duration"
            onChange={investmentDurationChangeHandler}
          />
        </p>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt" onClick={resetButtonHandler}>
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
}

export default Form;
